mapper.insert:
insert into sys_users (user_id, username, password, 
  role, avatar_url, phone, 
  email, created_time, updated_time
  )
values (#{userId,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{role,jdbcType=INTEGER}, #{avatarUrl,jdbcType=VARCHAR}, #{phone,jdbcType=INTEGER}, 
  #{email,jdbcType=VARCHAR}, #{createdTime,jdbcType=TIMESTAMP}, #{updatedTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select

from sys_users


    and user_id = #{userId}



    and username = #{handledUsername}


    and password = #{password}


    and role = #{role}


    and avator_url = #{avatorUrl}


    and phone = #{phone}


    and created_time between #{createdTimeFrom} and #{createdTimeTo}


    and updated_time between #{updatedTimeFrom} and #{updatedTimeTo}
